commits.content:
.calendar {
  display: flex;
  flex-wrap: wrap;
  position: absolute;
  gap: $gap;
  max-width: calc(
    #{$columns} * #{$cell-size} + #{$columns} * #{$gap} + 2 * #{$padding}
  );
  padding: $padding;
  cursor: pointer;

   &__day {
    width: $cell-size;
    height: $cell-size;
    border: $border-width solid black;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 30px;
    position: relative;
    background-color: #eee;
    font-family: Arial;
    transition: transform 0.5s;
  }

  &__day:hover {
    background-color: #ffbfcb;
    transform: translateY(-20px);
  }

 @for $i from 1 through 31 {
    &__day:nth-child(#{$i})::before {
      content: "#{$i}";
    }
  }

  &--start-day {
    @each $day, $i in $days {
      &-#{$day} > :first-child {
        margin-left: ($cell-size + $gap) * $i;
      }
    }
  }

    @for $n from 28 through 31 {
  &--month-length-#{$n} > :nth-child(n + #{$n + 1}) {
display: none;
    }
  }
}
